var tempElement = document.getElementById("temperature");
		var cityElement = document.getElementById("city");
		var typeElement = document.getElementById("type");
        var inCelsius = true;
        var tempInK = 0;
		var temp ="";
		var city="";
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(showPosition, showError);
        } else {
            tempElement.innerHTML = "Geolocation is not supported by this browser.";
        }
       
        function change(buttonObj){
                inCelsius = !inCelsius;
				
                if (inCelsius) {
                    temp = Math.round(tempInK - 273) + " &degC";
                    buttonObj.value = "F";
                } else {
                    temp = Math.round((tempInK - 273) * 1.8 + 32) + " &degF";
                    buttonObj.value = "C";
                }   
				tempElement.innerHTML = temp;
            }
		function changeBackground(type) {
			var type = type.toLowerCase();
			var imageURL = "";
			switch (type) {
			  case 'dizzle':
				imageURL = "https://encrypted-tbn1.gstatic.com/images?q=tbn:ANd9GcSJXuiIvL3iLDj2ljlwxHUVYYDMRjJ7zBFdfvTnLEVhAhPJL_kZxQ";
				break;
			  case 'clouds':
				imageURL ="data:image/jpeg;base64,";
				break;
			  case 'rain':
				imageURL="data:image/jpeg;base64,";
				break;
			  case 'snow':
				imageURL="data:image/jpeg;base64,";
				break;
			  case 'clear':
				imageURL="data:image/jpeg;base64,";
				break;
			  case 'thunderstom':
				imageURL="data:image/jpeg;base64,";
				break;
				case 'haze':
				imageURL="data:image/jpeg;base64,";
				break;
				case 'sunny':
				imageURL ="https://encrypted-tbn2.gstatic.com/images?q=tbn:ANd9GcSk5HYU3hcN64KGTXNQSvQlFAcTKzokiexbyEDBQrjS7fuAtCUm_Q";
				break;
				
			  default:
				imageURL="http://e3.365dm.com/16/10/1600x900/de07250cbb0a4381d907943d7a1f628934816478c8c443a7fc38a5402fa94a83_3813465.jpg?20161021120726";
			}
			document.body.style.backgroundImage = "url("+imageURL+")";
			document.body.style.backgroundSize ="cover";
		  }
        function showPosition(position) {
            var xhr = new XMLHttpRequest();
            var url = "http://api.openweathermap.org/data/2.5/weather?lat=" + position.coords.latitude + "&lon=" + position.coords.longitude + "&APPID=32c7cb12024f8537efdca6b2aebf26e4";
            xhr.open("GET", url, false);
            xhr.send();
 
            console.log(xhr.response);
            var res = JSON.parse(xhr.response);
            var type = res["weather"][0]["main"]; //Haze
			changeBackground(type);
            tempInK = res["main"]["temp"];
			city = res["name"];
            console.log("type: " + type);
            console.log("tempInK: " + tempInK);
			tempElement.innerHTML = Math.round(tempInK - 273) + " &degC";
			cityElement.innerHTML = city;
			typeElement.innerHTML = type;
        }
 
        function showError(error) {
            switch (error.code) {
                case error.PERMISSION_DENIED:
                    tempElement.innerHTML = "User denied the request for Geolocation."
                    break;
                case error.POSITION_UNAVAILABLE:
                    tempElement.innerHTML = "Location information is unavailable."
                    break;
                case error.TIMEOUT:
                    tempElement.innerHTML = "The request to get user location timed out."
                    break;
                case error.UNKNOWN_ERROR:
                    tempElement.innerHTML = "An unknown error occurred."
                    break;
            }
        }